void apagar(int xmin, int xmax, int ymin, int ymax)
{
    int i, j;
    gotoxy(xmin, ymax);
    for (i = xmin; i < (xmax); i++)
    {
        printf("%c", 255);
    }
    for (i = 1; i < (ymax); i++)
    {
        gotoxy((xmin), (ymin + i));
        for (j = 0; j < (xmax - xmin); j++)
        {
            printf("%c", 255);
        }
    }
    gotoxy(1, 1);
}
