void caixa(int xmin, int xmax, int ymin, int ymax, int cor)
{
    setcolor(cor);
    int i, j;
    gotoxy(xmin, ymax);
    for (i = xmin; i <= xmax; i++)
    {
        printf("%c", 205);
    }
    for (i = 0; i <= (ymax - 1); i++)
    {
        if (i == 1)
        {
            gotoxy(xmin - 1, ymin);
            printf("%c", 201);
        }
        if ((ymin + i) <= ymax)
        {
            gotoxy((xmin - 1), (ymin + i));
            printf("%c", 186);
        }
        if (i == (ymax - 2))
        {
            gotoxy(xmax + 1, ymin);
            printf("%c ", 187);
        }
    }
    for (i = 1; i < (ymax - 1); i++)
    {
        if (i == 1)
        {
            gotoxy(xmin - 1, ymax);
            printf("%c", 200);
        }
        if ((ymax - i) >= ymin)
        {
            gotoxy((xmax + 1), (ymin + i));
            printf("%c", 186);
        }
        if (i == (ymax - 2))
        {
            gotoxy((xmax + 1), ymax);
            printf("%c", 188);
        }
    }
    gotoxy(xmin, ymin);
    for (i = xmin; i <= xmax; i++)
    {
        printf("%c", 205);
    }
    setcolor(7);
}
