{
    int i, j;
    setcolor(cor);
    gotoxy(xmin, ymax);
    for (i = xmin; i < (xmax); i++)
    {
        printf("%c", 219);
    }
    for (i = 1; i < (ymax); i++)
    {
        gotoxy((xmin), (ymin + i));
        for (j = 0; j < (xmax - xmin); j++)
        {
            printf("%c", 219);
        }
    }
    setcolor(7);
    gotoxy(1, 1);
}
