void tela_configuracoes_labirinto(int y, int aux, int tamanho_mapa)
{
    gotoxy(32, 4);
    printf(" TAMANHO DO MAPA ");
    gotoxy(30, 5);
    printf("[-] <           > [+]");
    gotoxy(34 + aux, 5);
    printf("%c", 207);
    for (aux = 3; aux < tamanho_mapa; aux += 3)
    {
        gotoxy(34 + aux, 5);
        printf("%c", 207);
    }
    if (y != 18)
    {
        gotoxy(36, 18);
        printf(" TAMANHO  ");
    }
    else if (y == 18)
    {
        gotoxy(37, 18);
        printf(" TAMANHO  ");
    }
    if (y != 19)
    {
        gotoxy(36, 19);
        printf(" INICIAR  ");
    }
    else if (y == 19)
    {
        gotoxy(37, 19);
        printf(" INICIAR  ");
    }
    gotoxy(36, 25);
    printf(" VOLTAR  ");
    gotoxy(19, 29);
    printf("AO CLICAR NAS OPCOES, MUDA AS CONFIGURACOES");
}
