void configuracoes_labirinto()
{
    int x = 33, y = 19, tecla, sair = 0, aux = 3;
    gotoxy(24, 21);
    printf("            "); /* APAGAR AJUDA */
    system("title CONFIGURACOES - LABIRINTO");
    do
    {
        tela_configuracoes_labirinto(y, aux, tamanho_mapa);
        gotoxy(x, y);
        putchar('>');
        tecla = getch();
        switch (tecla)
        {
        case BAIXO:
            gotoxy(x, y);
            putchar(' ');
            if (y == 25)
            {
                y = 18;
            }
            else if (y == 19)
            {
                y = 25;
            }
            else
            {
                y++;
            }
            gotoxy(x, y);
            putchar('>');
            break;

        case CIMA:
            gotoxy(x, y);
            putchar(' ');
            if (y == 18)
            {
                y = 25;
            }
            else if (y == 25)
            {
                y = 19;
            }

            else
            {
                y--;
            }
            gotoxy(x, y);
            putchar('>');
            break;
        case ENTER:
            switch (y)
            {
            case 18:
                if (aux >= 9)
                {
                    aux = 3;
                    tamanho_mapa = 3;
                    gotoxy(35, 5);
                    printf("          ");
                    break;
                }
                aux += 3;
                tamanho_mapa += 3;
                break;
            case 19:
                system("cls");
                return 0;
                break;
            default:
                break;
            }
            tela_configuracoes_labirinto(y, aux, tamanho_mapa);
        }
    } while (1);
}
