/* >>----<< */
void controles_labirinto()
{
    int tecla, x = 6, y = 3, controller = 1;
    do
    {
        setcolor(4);
        gotoxy(x, y);
        printf("%c", 254);
        tecla = getch(); //capturamos a tecla digitada
        switch (tecla)
        {
        case BAIXO: /* > Se pressionarmos a tecla seta para BAIXO < */
            if ((verifica_labirinto((x - 5), (y - 2))) == 2)
            {
                controller = 0;
                break;
            }
            if ((verifica_labirinto((x - 5), ((y - 2) + 1)) != 1))
            {
                gotoxy(x, y);
                putchar(' ');
                y++;
                break;
            }
            break;
        case CIMA:
            if ((verifica_labirinto((x - 5), (y - 2))) == 2)
            {
                controller = 0;
                break;
            }
            if ((verifica_labirinto((x - 5), ((y - 2) - 1)) != 1))
            {
                gotoxy(x, y);
                putchar(' ');
                y--;
                break;
            }
            break;
        case DIREITA:
            if ((verifica_labirinto((x - 5), (y - 2))) == 2)
            {
                controller = 0;
                break;
            }
            if ((verifica_labirinto((x - 5) + 1, (y - 2)) != 1))
            {
                gotoxy(x, y);
                putchar(' ');
                x++;
                break;
            }
            break;
        case ESQUERDA:
            if ((verifica_labirinto((x - 5), (y - 2))) == 2)
            {
                controller = 0;
                break;
            }
            if ((verifica_labirinto(((x - 5) - 1), (y - 2)) != 1))
            {
                gotoxy(x, y);
                putchar(' ');
                x--;
                break;
            }
            break;
        }
        setcolor(7);
    } while (controller == 1);
}
